function saveBookmark(e){var t=document.getElementById("siteName").value,a=document.getElementById("siteUrl").value,o={name:t,url:a};if(null===localStorage.getItem("bookmarks")){var r=[];r.push(o),localStorage.setItem("bookmarks",JSON.stringify(r))}else{var r=JSON.parse(localStorage.getItem("bookmarks"));r.push(o),localStorage.setItem("bookmarks",JSON.stringify(r))}fetchBookmarks(),e.preventDefault()}function deleteBookmark(e){for(var t=JSON.parse(localStorage.getItem("bookmarks")),a=0;a<t.length;a++)t[a].url==e&&t.splice(a,1);localStorage.setItem("bookmarks",JSON.stringify(t)),fetchBookmarks()}function fetchBookmarks(){var e=JSON.parse(localStorage.getItem("bookmarks")),t=document.getElementById("bookmarksResults");t.innerHTML="";for(var a=0;a<e.length;a++){var o=e[a].name,r=e[a].url;t.innerHTML+='<div class="well"><h3>'+o+' <a class="btn btn-default" target="_blank" href="'+r+'">Visit</a>  <a onclick="deleteBookmark(\''+r+'\')" class="btn btn-danger" href="#">Delete</a> </h3></div>'}}document.getElementById("myForm").addEventListener("submit",saveBookmark);