function saveBookmark(e){var t=document.getElementById("siteName").value,a=document.getElementById("siteUrl").value;if(!validateForm(t,a))return!1;var r={name:t,url:a};if(null===localStorage.getItem("bookmarks")){var o=[];o.push(r),localStorage.setItem("bookmarks",JSON.stringify(o))}else{var o=JSON.parse(localStorage.getItem("bookmarks"));o.push(r),localStorage.setItem("bookmarks",JSON.stringify(o))}document.getElementById("myForm").reset(),fetchBookmarks(),e.preventDefault()}function deleteBookmark(e){for(var t=JSON.parse(localStorage.getItem("bookmarks")),a=0;a<t.length;a++)t[a].url==e&&t.splice(a,1);localStorage.setItem("bookmarks",JSON.stringify(t)),fetchBookmarks()}function fetchBookmarks(){var e=JSON.parse(localStorage.getItem("bookmarks")),t=document.getElementById("bookmarksResults");t.innerHTML="";for(var a=0;a<e.length;a++){var r=e[a].name,o=e[a].url;t.innerHTML+='<div class="well"><h3>'+r+' <a class="btn btn-default" target="_blank" href="'+o+'">Visit</a>  <a onclick="deleteBookmark(\''+o+'\')" class="btn btn-danger" href="#">Delete</a> </h3></div>'}}function validateForm(e,t){if(!e||!t)return alert("Please fill in the form"),!1;var a=/[-a-zA-Z0-9@:%_\+.~#?&\/\/=]{2,256}\.[a-z]{2,4}\b(\/[-a-zA-Z0-9@:%_\+.~#?&\/\/=]*)?/gi,r=new RegExp(a);return t.match(r)?!0:(alert("Please use a valid URL"),!1)}document.getElementById("myForm").addEventListener("submit",saveBookmark);