function saveBookmark(e){var a=document.getElementById("siteName").value,t=document.getElementById("siteUrl").value;if(!validateForm(a,t))return!1;var r={name:a,url:t};if(null===localStorage.getItem("bookmarks")){var o=[];o.push(r),localStorage.setItem("bookmarks",JSON.stringify(o))}else{var o=JSON.parse(localStorage.getItem("bookmarks"));o.push(r),localStorage.setItem("bookmarks",JSON.stringify(o))}fetchBookmarks(),e.preventDefault()}function deleteBookmark(e){for(var a=JSON.parse(localStorage.getItem("bookmarks")),t=0;t<a.length;t++)a[t].url==e&&a.splice(t,1);localStorage.setItem("bookmarks",JSON.stringify(a)),fetchBookmarks()}function fetchBookmarks(){var e=JSON.parse(localStorage.getItem("bookmarks")),a=document.getElementById("bookmarksResults");a.innerHTML="";for(var t=0;t<e.length;t++){var r=e[t].name,o=e[t].url;a.innerHTML+='<div class="well"><h3>'+r+' <a class="btn btn-default" target="_blank" href="'+o+'">Visit</a>  <a onclick="deleteBookmark(\''+o+'\')" class="btn btn-danger" href="#">Delete</a> </h3></div>'}}function validateForm(e,a){if(!e||!a)return alert("Please fill in the form"),!1;var t=/[-a-zA-Z0-9@:%_\+.~#?&\/\/=]{2,256}\.[a-z]{2,4}\b(\/[-a-zA-Z0-9@:%_\+.~#?&\/\/=]*)?/gi,r=new RegExp(t);return a.match(r)?!0:(alert("Please use a valid URL"),!1)}document.getElementById("myForm").addEventListener("submit",saveBookmark);